SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE db_new_version_content_builder ( nr_seq_content_builder_p bigint, nm_usuario_p text, nr_seq_new_version_p INOUT bigint) AS $body$
DECLARE


/*
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Objective: Generate a new version of the template
	------------------------------------------------------------------------
	Direct call: 
	[   ] Dictionary objects
	[ X ] Tasy (Delphi/Java/HTML5)
	[   ] Portal
	[   ] Reports
	[   ] Others:
	 -----------------------------------------------------------------------
	Attention points: N/A
	------------------------------------------------------------------------
	References:
		Tables:	db_content_builder
		Objects:
	------------------------------------------------------------------------
	Called in:
		Java: N/A
		HTML5:	corQua\corQuaGDP\controllers\wpumc\docBuilderWPUMC\DbContentBuilderNewVersionWJMI.js
		Object: N/A
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/
	nr_seq_w	db_content_build.nr_sequencia%type;
	nr_version_w	db_content_build.nr_version%type;
	

BEGIN

	select	cb.nr_seq_source
	into STRICT	nr_seq_w
	from	db_content_build cb
	where	cb.nr_sequencia = nr_seq_content_builder_p;
	
	--children record
	if (nr_seq_w IS NOT NULL AND nr_seq_w::text <> '') then
		select	max(cb.nr_version)
		into STRICT	nr_version_w
		from	db_content_build cb
		where	cb.nr_seq_source = nr_seq_w;
	else
		select	max(cb.nr_version)
		into STRICT	nr_version_w
		from	db_content_build cb
		where	cb.nr_seq_source = nr_seq_content_builder_p;
		
		if (coalesce(nr_version_w::text, '') = '') then
			select	max(cb.nr_version)
			into STRICT	nr_version_w
			from	db_content_build cb
			where	cb.nr_sequencia = nr_seq_content_builder_p;
		end if;
	end if;

	select	nextval('db_content_build_seq')
	into STRICT	nr_seq_w
	;

	insert
	into db_content_build(
			nr_sequencia,
			nr_seq_content_template,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_version,
			ds_description,
			ds_detail,
			nr_seq_source
		)
	SELECT	nr_seq_w,
		cb.nr_seq_content_template,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_version_w + 1,		
		cb.ds_description,
		cb.ds_detail,
		coalesce(cb.nr_seq_source, nr_seq_content_builder_p)
	from	db_content_build cb
	where	cb.nr_sequencia = nr_seq_content_builder_p;
	
	insert
	into	db_content_build_par_value(
			nr_sequencia,
			nr_seq_content_build,
			nr_seq_content_macro_par,
			vl_parameter,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec
		)
	SELECT	nextval('db_content_build_par_value_seq'),
		nr_seq_w,
		nr_seq_content_macro_par,
		vl_parameter,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p
	from	db_content_build_par_value
	where	nr_seq_content_build = nr_seq_content_builder_p;

	commit;

	nr_seq_new_version_p := nr_seq_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE db_new_version_content_builder ( nr_seq_content_builder_p bigint, nm_usuario_p text, nr_seq_new_version_p INOUT bigint) FROM PUBLIC;

