CREATE OR REPLACE PROCEDURE db_refresh_build_param_value ( nr_seq_content_build_p db_content_build.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Objective: N/A
	------------------------------------------------------------------------
	Direct call: 
	[   ] Dictionary objects
	[   ] Tasy (Delphi/Java/HTML5)
	[   ] Portal
	[   ] Reports
	[   ] Others:
	 -----------------------------------------------------------------------
	Attention points: N/A
	------------------------------------------------------------------------
	References:
		Tables: N/A
		Objects: N/A
	------------------------------------------------------------------------
	Called in:
		Java: N/A
		HTML5: N/A
		Object: N/A
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/
nr_seq_content_build_w	db_content_build.nr_sequencia%type;


BEGIN


	select	max(nr_sequencia)
	into STRICT	nr_seq_content_build_w
	from	db_content_build cb
	where	cb.nr_sequencia = nr_seq_content_build_p;


	if (nr_seq_content_build_w IS NOT NULL AND nr_seq_content_build_w::text <> '') then
		begin
			delete
			from	db_content_build_par_value
			where	nr_seq_content_build = nr_seq_content_build_w;
			
			commit;

			CALL db_add_build_param_value(	nr_seq_content_build_p	=> nr_seq_content_build_p,
							nm_usuario_p		=> nm_usuario_p);
		end;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
