CREATE OR REPLACE PROCEDURE db_workflow_document_update ( nm_usuario_p text, nr_seq_workflow_p bigint, nr_seq_workflow_item_p bigint, nr_seq_document_p bigint, ie_status_p text) AS $body$
BEGIN
	
	update 	db_workflow_item
	set 	nr_seq_documento = nr_seq_document_p,
		ie_status = ie_status_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where 	nr_seq_workflow = nr_seq_workflow_p
	and 	nr_sequencia = nr_seq_workflow_item_p;
	
	if (nr_seq_document_p IS NOT NULL AND nr_seq_document_p::text <> '') then
	
		update 	db_document
		set	nr_seq_wf_item = nr_seq_workflow_item_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_seq_document_p;
	
	end if;
	
	commit;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
