SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE db_workflow_template_version ( nr_seq_workflow_template_p bigint, nm_usuario_p text, nr_seq_nova_versao_p INOUT bigint) AS $body$
DECLARE


nr_seq_superior_w	db_workflow_template.nr_seq_superior%type;
nr_seq_template_w	db_workflow_template.nr_sequencia%type;
nr_versao_w		db_workflow_template.nr_versao%type;
nr_seq_item_w		db_workflow_template_item.nr_sequencia%type;

c01 CURSOR FOR	
	SELECT	item.nr_sequencia,
		item.dt_atualizacao,
		item.dt_atualizacao_nrec,
		item.nm_usuario,
		item.nm_usuario_nrec,
		item.nr_seq_doc_template,
		nr_seq_template_w,
		ds_prefixo_id,
		ie_obrigatorio
	from	db_workflow_template_item item
	where	item.nr_seq_wf_template = nr_seq_superior_w;

BEGIN

select	coalesce(nr_seq_superior, nr_seq_workflow_template_p)
into STRICT	nr_seq_superior_w
from	db_workflow_template a
where	nr_sequencia = nr_seq_workflow_template_p;

--child records
if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
	select	max(nr_versao)
	into STRICT	nr_versao_w
	from	db_workflow_template a
	where	coalesce(nr_seq_superior, nr_sequencia) = nr_seq_superior_w;

	select	nextval('db_workflow_template_seq')
	into STRICT	nr_seq_template_w
	;

	insert into db_workflow_template(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_template,
		nr_versao,
		nr_seq_superior)
	SELECT	nr_seq_template_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_template,
		nr_versao_w + 1,
		nr_seq_superior_w
	from	db_workflow_template a
	where	nr_sequencia = nr_seq_superior_w;
	
	
	for c01_w in c01 loop
		begin
		
		select	nextval('db_workflow_template_item_seq')
		into STRICT	nr_seq_item_w
		;

		insert
		into db_workflow_template_item(
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_doc_template,
			nr_seq_wf_template,
			ds_prefixo_id,
			ie_obrigatorio,
			nr_sequencia
			)
		values (
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			c01_w.nr_seq_doc_template,
			nr_seq_template_w,
			c01_w.ds_prefixo_id,
			c01_w.ie_obrigatorio,
			nr_seq_item_w);
		
		insert into db_wf_templ_item_predec(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_item,
			nr_seq_doc_template)
		SELECT	nextval('db_wf_templ_item_predec_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_item_w,
			a.nr_seq_doc_template
		from	db_wf_templ_item_predec a
		where	a.nr_seq_item = c01_w.nr_sequencia;
		
		end;
	end loop;
	
commit;
end if;

nr_seq_nova_versao_p := nr_seq_template_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
