CREATE OR REPLACE PROCEDURE dcx_inserir_imagem_laudo ( nr_seq_laudo_p bigint, ds_arquivo_imagem_p text, nm_usuario_p text, ds_imagem_p text ) AS $body$
DECLARE


ie_existe_w	varchar(1);
nr_seq_imagem_w	laudo_paciente_imagem.nr_seq_imagem%TYPE;


BEGIN

SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
INTO STRICT 	ie_existe_w
FROM 	laudo_paciente_imagem a
WHERE 	a.nr_sequencia = nr_seq_laudo_p
AND  	a.ds_arquivo_imagem = ds_arquivo_imagem_p;

IF (ie_existe_w = 'N') THEN

	SELECT	coalesce(MAX(a.nr_seq_imagem),0)+1
	INTO STRICT 	nr_seq_imagem_w
	FROM 	laudo_paciente_imagem a
	WHERE 	a.nr_sequencia = nr_seq_laudo_p;

	INSERT INTO laudo_paciente_imagem(
		nr_sequencia,
		nr_seq_imagem,
		ds_arquivo_imagem,
		ds_imagem,
		nm_usuario,
		dt_atualizacao)
	VALUES ( nr_seq_laudo_p,
		nr_seq_imagem_w,
		ds_arquivo_imagem_p,
		ds_imagem_p,
		nm_usuario_p,
		clock_timestamp());
	COMMIT;
END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
