CREATE OR REPLACE PROCEDURE dc_save_ihc_service ( nr_service_code_p text, ds_service_description_p text, qt_number_of_services_p text, vl_charge_amount_p bigint, vl_fund_benefit_amount_p bigint, dt_from_date_p timestamp, dt_to_date_p timestamp, dt_service_date_p timestamp, nr_invoice_id_p bigint, nr_claim_id_p text, nm_usuario_p text) AS $body$
