from    era_response
where   invoice_id = nr_invoice_id_p
and     nr_claim_id = nr_claim_id_p;



if (ie_exists_response_w = 0) then

  insert into dc_ihc_service(
              nr_sequencia,
              dt_atualizacao,
              nm_usuario,
