              ds_service_description,
              vl_charge_amount,
              vl_fund_benefit_amount,
              dt_from,
              dt_to,
              dt_service,
              nr_invoice_id,
              nr_claim_id
            )
            values (
              nextval('dc_ihc_service_seq'),
              clock_timestamp(),
              nm_usuario_p,
              clock_timestamp(),
              nm_usuario_p,
              nr_service_code_p,
              ds_service_description_p,
              vl_charge_amount_p,
              vl_fund_benefit_amount_p,
              dt_from_date_p,
              dt_to_date_p,
