CREATE OR REPLACE PROCEDURE define_atend_retorno_eup_js ( nr_atendimento_p bigint, nr_atend_retorno_p INOUT bigint, ds_mensagem_p INOUT text, ds_mensagem_sec_p INOUT text, ie_verifica_retorno_p INOUT text, ds_mensagem_pergunta_p INOUT text, nr_seq_regra_msg_p INOUT bigint) AS $body$
DECLARE

 
nr_atend_retorno_w		bigint;
ds_convenio_w		varchar(255);
dt_entrada_w		varchar(255);
nm_medico_w		varchar(255);
ds_mensagem_w		varchar(255);
ds_mensagem_sec_w	varchar(255);
ie_verifica_retorno_w	varchar(1);
ds_mensagem_pergunta_w	varchar(255);
nr_seq_msg_ret_w bigint;		

BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0)then 
	begin 
	 
	SELECT * FROM define_atendimento_retorno(nr_atendimento_p, nr_atend_retorno_w, nr_seq_msg_ret_w) INTO STRICT nr_atend_retorno_w, nr_seq_msg_ret_w;
	 
	if (nr_atend_retorno_w > 0)then 
		begin 
		 
		ds_convenio_w	:= obter_dados_atendimento(nr_atend_retorno_w, 'NC');
		dt_entrada_w	:= obter_dados_atendimento(nr_atend_retorno_w, 'DE');
		nm_medico_w	:= obter_dados_atendimento(nr_atend_retorno_w, 'NMR');
		 
		/*ds_mensagem_w	:= ' Confirma retorno do atendimento ? ' || chr(10) || 
				  ' Nº Atendimento: ' || nr_atend_retorno_w || chr(10) || 
				  ' Convênio: ' || ds_convenio_w || chr(10) || 
				  ' Médico: ' || nm_medico_w || chr(10) || 
				  ' Data entrada: ' || dt_entrada_w;*/
 
 
		ds_mensagem_w := Wheb_mensagem_pck.get_texto(307251, 'NR_ATEND_RETORNO_W='||nr_atend_retorno_w||';DS_CONVENIO_W='|| 
								  ds_convenio_w||';NM_MEDICO_W='||nm_medico_w||';DT_ENTRADA_W='||dt_entrada_w);
		 
		/*ds_mensagem_sec_w	:= 	'Retorno do atendimento: ' || chr(10) || 
						' Nº Atendimento: ' || nr_atend_retorno_w || chr(10) || 
						' Convênio: ' || ds_convenio_w || chr(10) || 
						' Médico: ' || nm_medico_w || chr(10) || 
						' Data entrada: ' || dt_entrada_w;*/
 
		 
		ds_mensagem_sec_w := Wheb_mensagem_pck.get_texto(307252, 'NR_ATEND_RETORNO_W='||nr_atend_retorno_w||';DS_CONVENIO_W='|| 
		                    				      ds_convenio_w||';NM_MEDICO_W='||nm_medico_w||';DT_ENTRADA_W='||dt_entrada_w);
			 
		 
		end;
	end if;
	 
	ie_verifica_retorno_w	:= sus_verifica_retorno_aih(nr_atendimento_p);
	ds_mensagem_pergunta_w	:= obter_texto_dic_objeto(286270, 1, 'NR_ATENDIMENTO_P=' ||nr_atendimento_p);
	end;
end if;
 
nr_atend_retorno_p	:= coalesce(nr_atend_retorno_w,0);
ds_mensagem_p		:= ds_mensagem_w;
ds_mensagem_sec_p	:= ds_mensagem_sec_w;
ie_verifica_retorno_p	:= ie_verifica_retorno_w;
ds_mensagem_pergunta_p	:= ds_mensagem_pergunta_w;
nr_seq_regra_msg_p := nr_seq_msg_ret_w;
 
commit;
 
end;
