SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE define_convenio_atendimento ( nr_atendimento_p bigint, cd_convenio_agenda_p bigint, nm_usuario_p text, nr_seq_interno_p INOUT bigint) AS $body$
DECLARE

 
ie_tipo_convenio_w		smallint;
ds_parametro_w			varchar(255);


BEGIN 
 
nr_seq_interno_p		:= 0;
ds_parametro_w			:= null;
 
select	max(ie_tipo_convenio) 
into STRICT	ie_tipo_convenio_w 
from 	estabelecimento b, 
	Atendimento_paciente a 
where	a.cd_estabelecimento	= b.cd_estabelecimento 
and 	a.nr_atendimento	= nr_atendimento_p;
 
if (ie_tipo_convenio_w in (2,3,4,5,6,7,8,9,10)) then 
	select	max(vl_parametro) 
	into STRICT	ds_parametro_w 
	from 	funcao_parametro 
	where	cd_funcao	= 0 
	and	nr_sequencia	= 31;
end if;
 
if (ds_parametro_w IS NOT NULL AND ds_parametro_w::text <> '') then 
	nr_seq_interno_p	:= -1;
else 
	/* Edgar 23/11/2004 OS 13025, fiz a procedure GERAR_CONVENIO_ATENDIMENTO para gerar convenio*/
 
	nr_seq_interno_p := GERAR_CONVENIO_ATENDIMENTO( 
			nr_atendimento_p, cd_convenio_agenda_p, nm_usuario_p, nr_seq_interno_p);
end if;
 
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
