SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE define_convenio_atend_agenda (cd_pessoa_fisica_p text, nm_usuario_p text, cd_convenio_p INOUT bigint, cd_categoria_p INOUT text, dt_validade_p INOUT timestamp, cd_usuario_convenio_p INOUT text, nr_doc_convenio_p INOUT text, cd_tipo_acomodacao_p INOUT bigint, cd_plano_p INOUT text) AS $body$
DECLARE


nr_atendimento_w		bigint;	
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
dt_validade_w			timestamp;
cd_usuario_convenio_w	varchar(30);
nr_doc_convenio_w		varchar(20);
cd_tipo_acomodacao_w		smallint;
ie_particular_w		varchar(1);
ie_tipo_convenio_w		smallint := -1;
cd_plano_w			varchar(10);


BEGIN
/* Rafael em 03/03/2007 OS51095 */

select	coalesce(max(obter_valor_param_usuario(0, 72, obter_perfil_ativo, nm_usuario_p, 0)),'N')
into STRICT	ie_particular_w
;

if (ie_particular_w = 'S') then
	ie_tipo_convenio_w := 1;
end if;


/* obter atendimento */

select	coalesce(max(a.nr_atendimento),0)
into STRICT	nr_atendimento_w
from	convenio c,
	atend_categoria_convenio b,
	atendimento_paciente a
where	c.ie_tipo_convenio <> ie_tipo_convenio_w
and	c.cd_convenio = b.cd_convenio
and	b.nr_atendimento = a.nr_atendimento
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p;


/* obter dados convÃªnio */

select	max(b.cd_convenio),
	max(CASE WHEN coalesce(d.ie_situacao,'A')='A' THEN  b.cd_categoria  ELSE null END ),
	max(b.dt_validade_carteira),
	max(b.cd_usuario_convenio),
	max(b.nr_doc_convenio),
	max(b.cd_tipo_acomodacao),
	max(CASE WHEN coalesce(e.ie_situacao,'A')='A' THEN  b.cd_plano_convenio  ELSE null END )
into STRICT	cd_convenio_w,
	cd_categoria_w,
	dt_validade_w,
	cd_usuario_convenio_w,
	nr_doc_convenio_w,
	cd_tipo_acomodacao_w,
	cd_plano_w
FROM categoria_convenio d, convenio c, atendimento_paciente a, atend_categoria_convenio b
LEFT OUTER JOIN convenio_plano e ON (b.cd_convenio = e.cd_convenio AND b.cd_plano_convenio = e.cd_plano)
WHERE c.cd_convenio = b.cd_convenio and b.nr_atendimento = a.nr_atendimento and b.cd_convenio	= d.cd_convenio and b.cd_categoria = d.cd_categoria   and a.nr_atendimento = nr_atendimento_w and b.nr_seq_interno = (	SELECT	max(x.nr_seq_interno)
				from	convenio y,
					atend_categoria_convenio x
				where	y.ie_tipo_convenio <> ie_tipo_convenio_w
				and	y.cd_convenio = x.cd_convenio
				and	x.nr_atendimento = nr_atendimento_w) and coalesce(c.ie_situacao, 'A') = 'A';

cd_convenio_p := cd_convenio_w;
cd_categoria_p := cd_categoria_w;
dt_validade_p := dt_validade_w;
cd_usuario_convenio_p := cd_usuario_convenio_w;
nr_doc_convenio_p := nr_doc_convenio_w;
cd_tipo_acomodacao_p := cd_tipo_acomodacao_w;
cd_plano_p	:= cd_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
