CREATE OR REPLACE PROCEDURE define_estagio_revisao ( nr_seq_revisao_p bigint, nr_seq_estagio_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estagio_w	bigint;
nr_seq_classif_w	bigint;
nr_Seq_estagio_atual_w	bigint;
ie_nova_ordem_w		bigint;
cd_estabelecimento_w	integer := wheb_usuario_pck.Get_cd_estabelecimento;
ie_permite_loc_revisao_w	varchar(5) := 'S';
nr_seq_proc_interno_w	proc_interno.nr_sequencia%type;
ie_status_w		estagio_revisao_proc_int.ie_status%type;


BEGIN

ie_permite_loc_revisao_w := obter_param_usuario(947, 75, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_permite_loc_revisao_w);

select	coalesce(max(nr_seq_classif),0),
	coalesce(max(nr_Seq_estagio),0),
	coalesce(max(nr_seq_proc_interno),0)
into STRICT	nr_seq_classif_w,
	nr_Seq_estagio_atual_w,
	nr_seq_proc_interno_w
from 	proc_interno_revisao
where 	nr_sequencia = nr_seq_revisao_p
and 	coalesce(dt_liberacao::text, '') = '';

select	max(ie_status)
into STRICT	ie_status_w
from	estagio_revisao_proc_int
where	nr_sequencia = nr_seq_estagio_p;

if (nr_seq_classif_w > 0) then

	if (coalesce(ie_status_w,'YZ') = 'E') then

		insert into proc_interno_revisao_log(
				nr_sequencia,
				nr_seq_revisao,
				dt_atualizacao,
				nm_usuario,
				nr_seq_estagio_ant,
				nr_seq_estagio_atual)
			values (nextval('proc_interno_revisao_log_seq'),
				nr_seq_revisao_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_Seq_estagio_atual_w,
				nr_seq_estagio_p);

		update	proc_interno_revisao
		set 	dt_liberacao = clock_timestamp(),
			nr_seq_estagio = nr_seq_estagio_p
		where 	nr_sequencia = nr_seq_revisao_p;
	else

		insert into proc_interno_revisao_log(
				nr_sequencia,
				nr_seq_revisao,
				dt_atualizacao,
				nm_usuario,
				nr_seq_estagio_ant,
				nr_seq_estagio_atual)
			values (nextval('proc_interno_revisao_log_seq'),
				nr_seq_revisao_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_Seq_estagio_atual_w,
				nr_seq_estagio_p);

		update	proc_interno_revisao
		set 	nr_seq_estagio = nr_seq_estagio_p
		where 	nr_sequencia = nr_seq_revisao_p;
	end if;

	if (coalesce(ie_permite_loc_revisao_w,'S') = 'N') then

		update	proc_interno
		set	IE_LOCALIZADOR 	= 'S',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao  = clock_timestamp()
		where	nr_sequencia	= nr_seq_proc_interno_W
		and	IE_LOCALIZADOR  = 'N';
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
