CREATE OR REPLACE PROCEDURE define_imagem_exame ( nr_seq_interno_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_Seq_interno_p IS NOT NULL AND nr_Seq_interno_p::text <> '') then

	update 	prescr_procedimento
	set	ie_imagem_pacs = CASE WHEN ie_opcao_p='S' THEN 'S'  ELSE 'N' END ,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_interno = nr_seq_interno_p
	and	coalesce(ie_imagem_pacs,'N') <> 'S';

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
