c01 CURSOR FOR
  SELECT  a.nr_sequencia,
    a.cd_material,
    coalesce(a.ie_urgencia,'N'),
    dividir(a.qt_total_dispensar,b.qt_conv_estoque_consumo),
    substr(obter_se_material_padronizado(k.cd_estabelecimento,a.cd_material),1,1),
    substr(obter_se_medic_controlado(a.cd_material),1,1),
    coalesce(a.ie_acm, 'N'),
    coalesce(a.ie_se_necessario, 'N'),
    a.cd_unidade_medida_dose,
    a.ie_via_aplicacao,
    b.ie_tipo_material,
    b.ie_multidose,
    b.ie_termolabil,
    b.cd_material_generico,
    a.qt_dose,
    obter_volume_dil_regra_local(a.nr_prescricao, a.nr_sequencia),
    a.nr_seq_recomendacao,
    a.ie_agrupador,
    a.nr_seq_mat_diluicao,
    a.cd_intervalo
  from  material b,
    prescr_material a,
    prescr_medica k
  where  k.nr_prescricao    = nr_prescricao_p
  and  ((coalesce(nr_seq_material_p,0) = 0) or (a.nr_sequencia     = nr_seq_material_p) or (a.nr_sequencia_diluicao  = nr_seq_material_p) or (a.nr_seq_kit    = nr_seq_material_p))
  and  k.nr_prescricao    = a.nr_prescricao
  and  a.cd_material    = b.cd_material
  and ((ie_ajusta_lote_proc_etiqueta_w in ('S','N')) or (ie_ajusta_lote_proc_etiqueta_w = 'L' and a.ie_agrupador not in (16,17)));

c02 CURSOR FOR
