C04 CURSOR FOR
	SELECT	a.nr_sequencia
	from	prescr_mat_hor a,
		prescr_material b
	where 	a.nr_prescricao = b.nr_prescricao
	and 	a.nr_seq_material = b.nr_sequencia
	and 	(a.nr_seq_classif IS NOT NULL AND a.nr_seq_classif::text <> '')
	and  	a.nr_prescricao = nr_prescricao_p
	and  	((coalesce(a.nr_seq_lote::text, '') = '') or (exists ( SELECT 1 from ap_lote x where x.nr_sequencia = a.nr_seq_lote and x.ie_status_lote = 'G' )))
	order by b.nr_sequencia;


BEGIN
begin

select  max(cd_setor_atendimento),
    coalesce(max(cd_estabelecimento), 1),
    max(coalesce(coalesce(dt_liberacao_medico, dt_liberacao), clock_timestamp())),
    coalesce(max(cd_local_estoque), 0),
    coalesce(max(nr_atendimento), 0),
    max(ie_motivo_prescricao),
    max(ie_prescr_emergencia),
    coalesce(max(ie_emergencia), 'N')
into STRICT  cd_setor_atendimento_w,
    cd_estabelecimento_w,
    dt_liberacao_w,
    cd_local_estoque_prescr_w,
    nr_atendimento_w,
    ie_motivo_prescricao_w,
    ie_prescr_emergencia_w,
    ie_emergencia_w
from  prescr_medica
where  nr_prescricao = nr_prescricao_p;

ie_local_horario_w := obter_param_usuario(924, 851, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_local_horario_w);
ie_ajusta_lote_proc_etiqueta_w := obter_param_usuario(3111, 73, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_ajusta_lote_proc_etiqueta_w);
ie_setor_procedimento_w := obter_param_usuario(924, 1188, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_setor_procedimento_w);

ie_ajusta_lote_proc_etiqueta_w := coalesce(trim(both ie_ajusta_lote_proc_etiqueta_w),'N');

---

/*
IF upper(nm_usuario_p) = upper('lraju') Then
   ie_type_of_prescription_w := 'INPAT';
end if;
*/

---
if (ie_local_horario_w = 'S') then

  open C04;
  loop
  fetch C04 into
    nr_seq_mat_hor_w;
  EXIT WHEN NOT FOUND; /* apply on C04 */
    begin
    CALL define_disp_prescr_mat_hor(nr_seq_mat_hor_w, nr_prescricao_p, nr_seq_material_p, cd_perfil_p, nm_usuario_p);
    end;
  end loop;
  close C04;
end if;

ie_setor_atual_w := obter_param_usuario(924, 799, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_setor_atual_w);
if (ie_setor_atual_w = 'S') then
  cd_local_estoque_prescr_w := null;
  cd_setor_atendimento_w := obter_setor_atendimento(nr_atendimento_w);
elsif (ie_setor_atual_w = 'R') then
  cd_local_estoque_prescr_w := null;
  cd_setor_atendimento_w := obter_setor_atendimento_lib(nr_atendimento_w);
elsif (ie_setor_atual_w = 'I') then
  cd_local_estoque_prescr_w := null;
  cd_setor_atendimento_w := obter_setor_atepacu(obter_atepacu_paciente(nr_atendimento_w,'IA'),0);
end if;

cd_setor_atendimento_regra_w := cd_setor_atendimento_w;

if (cd_local_estoque_prescr_w > 0) then
  update  prescr_material
  set    cd_local_estoque = cd_local_estoque_prescr_w,
      nm_usuario = nm_usuario_p,
      nr_seq_regra_local  = NULL
  where  nr_prescricao = nr_prescricao_p
  and    coalesce(cd_motivo_baixa,0) = 0
  and    ((coalesce(nr_seq_material_p,0) = 0) or (nr_sequencia = nr_seq_material_p) or (nr_sequencia_diluicao = nr_seq_material_p) or (nr_seq_kit = nr_seq_material_p))
  and   ((ie_ajusta_lote_proc_etiqueta_w in ('S','N')) or (ie_ajusta_lote_proc_etiqueta_w = 'L' and ie_agrupador not in (16,17)));
else
  begin

  begin
  select  max(cd_local_estoque)
  into STRICT  cd_loc_estoque_w
  from  setor_atendimento
  where  cd_setor_atendimento = cd_setor_atendimento_w;
  exception
  when others then
    cd_loc_estoque_w := null;
  end;

  select  count(*)
  into STRICT  nr_regras_w
  from  regra_local_dispensacao where    cd_estabelecimento = cd_estabelecimento_w LIMIT 1;

  if (nr_regras_w > 0) then
    begin
    if (nr_atendimento_w > 0) then
      select  coalesce(max(ie_tipo_convenio),0),
          coalesce(max(nr_seq_forma_chegada),0),
          coalesce(max(ie_tipo_atendimento),0),
		  coalesce(max(nr_seq_local_pa),0)
      into STRICT  ie_tipo_convenio_w,
          nr_seq_forma_chegada_w,
          ie_tipo_atendimento_w,
		  nr_seq_local_pa_w
      from  atendimento_paciente
      where  nr_atendimento = nr_atendimento_w;

      cd_convenio_w := coalesce(obter_convenio_atendimento(nr_atendimento_w),0);
    end if;

    ie_local_estoque_kit_w := obter_param_usuario(924, 673, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_local_estoque_kit_w);
    ie_local_estoque_dil_w := obter_param_usuario(924, 741, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_local_estoque_dil_w);

    ie_dia_semana_w  := pkg_date_utils.get_WeekDay(dt_liberacao_w);

    if (coalesce(obter_se_feriado(cd_estabelecimento_w, dt_liberacao_w),0) = 0) then
      ie_feriado_w  := 'N';
    else
      ie_feriado_w  := 'S';
    end if;

    select  max(ie_tipo_hemodialise)
    into STRICT  ie_tipo_hemodialise_w
    from  prescr_solucao a,
        prescr_material b,
        hd_prescricao c
    where   b.nr_sequencia_solucao = a.nr_seq_solucao
    and    a.nr_prescricao = b.nr_prescricao
    and    a.nr_seq_dialise = c.nr_sequencia
    and    b.nr_prescricao = nr_prescricao_p
    and   coalesce(ie_hemodialise,'N') = 'S';

    open c01;
    loop
    fetch c01 into
      nr_sequencia_w,
      cd_material_w,
      ie_urgencia_w,
      qt_total_dispensar_w,
      ie_padronizado_w,
      ie_controlado_w,
      ie_acm_w,
      ie_se_necessario_w,
      cd_unidade_medida_w,
      ie_via_aplicacao_w,
      ie_tipo_material_w,
      ie_multidose_w,
      ie_termolabil_w,
      cd_material_generico_w,
      qt_dose_w,
      qt_volume_w,
      nr_seq_recomendacao_w,
      ie_agrupador_w,
      nr_seq_mat_diluicao_w,
      cd_intervalo_w;
    EXIT WHEN NOT FOUND; /* apply on c01 */
    --JRS712 starts
	cd_setor_atendimento_w := cd_setor_atendimento_regra_w;
    ie_type_of_prescription_w := '##';
    begin
      select coalesce(c.SI_TYPE_OF_PRESCRIPTION,'##') into STRICT ie_type_of_prescription_w
      from prescr_material a, cpoe_material b, cpoe_order_unit c 
        where b.NR_SEQ_CPOE_ORDER_UNIT = c.nr_sequencia 
          and b.nr_sequencia = a.nr_seq_mat_cpoe
          and a.nr_sequencia = nr_sequencia_w
          and a.nr_prescricao = nr_prescricao_p;
    exception
      when others then
        ie_type_of_prescription_w := '##';
    end;
    --JRS712 ends
      begin

	if (ie_setor_procedimento_w = 'S') then
		select	coalesce(max(b.cd_setor_atendimento),0)
		into STRICT	cd_setor_proced_w
		from	prescr_procedimento b,
				prescr_material a
		where 	a.nr_prescricao = b.nr_prescricao
		and 	a.nr_sequencia_proc = b.nr_sequencia
		and 	a.nr_prescricao = nr_prescricao_p
		and 	a.nr_sequencia = nr_sequencia_w;

		if (cd_setor_proced_w > 0) then
			cd_setor_atendimento_w := cd_setor_proced_w;
			
			begin
			select	max(cd_local_estoque)
			into STRICT	cd_loc_estoque_w
			from	setor_atendimento
			where	cd_setor_atendimento = cd_setor_atendimento_w;
			exception
			when others then
				cd_loc_estoque_w := null;
			end;
		end if;
	end if;

      select  max(ie_gerar_lote_manipulacao)
      into STRICT  ie_gerar_lote_manipulacao_w
      from  material_diluicao b
      where  	b.cd_material = cd_material_w
			and	exists (
			  SELECT  1
        from  prescr_material x
        where  x.nr_prescricao = nr_prescricao_p
        and    x.nr_sequencia_diluicao = nr_sequencia_w
        and    x.nr_seq_mat_diluicao = b.nr_sequencia);

      nr_seq_regra_local_w := null;

      cd_local_estoque_w := 0;

      select  cd_grupo_material,
          cd_subgrupo_material,
          cd_classe_material,
          nr_seq_familia
      into STRICT  cd_grupo_material_w,
          cd_subgrupo_w,
          cd_classe_material_w,
          nr_seq_familia_w
      from  estrutura_material_v
      where   cd_material = cd_material_w;

      select  coalesce(max('S'),'N')
      into STRICT  ie_intervalo_hor_w
      from  intervalo_prescricao
      where  cd_intervalo = cd_intervalo_w
      and  ie_acm <> 'S'
      and  ie_se_necessario <> 'S';

      if (ie_feriado_w = 'S') then
        /*anderson em 20/05/2009 - os142384 */

        select  count(1)
        into STRICT  qt_feriado_w
        from  regra_local_dispensacao where  cd_estabelecimento          = cd_estabelecimento_w
        and  coalesce(cd_setor_atendimento, cd_setor_atendimento_w)  = cd_setor_atendimento_w
        and  coalesce(cd_grupo_material, cd_grupo_material_w)    = cd_grupo_material_w
        and  coalesce(cd_subgrupo_material, cd_subgrupo_w)    = cd_subgrupo_w
        and  coalesce(cd_classe_material, cd_classe_material_w)    = cd_classe_material_w
        and  coalesce(cd_material, cd_material_w)      = cd_material_w
        and  coalesce(cd_perfil, cd_perfil_p)        = cd_perfil_p
        and  coalesce(cd_convenio, cd_convenio_w)      = cd_convenio_w
        and  ((coalesce(ie_via_aplicacao::text, '') = '') or (ie_via_aplicacao = ie_via_aplicacao_w))
        and  coalesce(ie_tipo_convenio, ie_tipo_convenio_w)    = ie_tipo_convenio_w
		and (coalesce(ie_type_of_prescription, ie_type_of_prescription_w) = ie_type_of_prescription_w OR coalesce(pkg_i18n.get_user_locale, 'pt_BR') <> 'ja_JP')  --JRS712
        and  coalesce(nr_seq_forma_chegada, nr_seq_forma_chegada_w)  = nr_seq_forma_chegada_w
        and  ((coalesce(ie_agora,'N') = ie_urgencia_w) or (coalesce(ie_agora,'N') = 'N'))
        and  coalesce(ie_motivo_prescricao, coalesce(ie_motivo_prescricao_w,'XPT'))  = coalesce(ie_motivo_prescricao_w,'XPT')
        and  ((coalesce(ie_motivo_prescr_exc::text, '') = '') or (ie_motivo_prescr_exc <> ie_motivo_prescricao_w))
        and	((coalesce(ie_considerar_emergencia,'S') = 'S') or (coalesce(ie_emergencia_w,'N') = 'N'))
        --and  ((nvl(ie_nao_padronizado,'N') = 'N') or (nvl(ie_padronizado_w,'S') = 'S')) Juan 11/09
        and  ((coalesce(ie_controlado,'A') = 'A') or (coalesce(ie_controlado, 'A') = ie_controlado_w))
        and  ((coalesce(ie_so_com_estoque,'N') = 'N')  or (obter_saldo_disp_estoque(cd_estabelecimento_w,cd_material_w,cd_local_estoque,clock_timestamp()) >= qt_total_dispensar_w))
        and  ((coalesce(nr_seq_familia, nr_seq_familia_w)      = nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = ''))
        and  ((coalesce(ie_dia_semana::text, '') = '') or (ie_dia_semana = ie_dia_semana_w or ie_dia_semana = 9))
        and  obter_se_lib_prescr_horario(dt_hora_inicio, dt_hora_fim, dt_liberacao_w) = 'S'
        and  coalesce(ie_feriado,'N') = 'S'
        and  (((coalesce(ie_somente_acmsn,'N') = 'G') and ((ie_acm_w = 'S') or (ie_se_necessario_w = 'S') or (ie_urgencia_w = 'S'))) or
           ((coalesce(ie_somente_acmsn,'N') = 'S') and ((ie_acm_w = 'S') or (ie_se_necessario_w = 'S'))) or
           ((coalesce(ie_somente_acmsn,'N') = 'A') and (ie_acm_w = 'S') and (ie_se_necessario_w = 'N')) or
           ((coalesce(ie_somente_acmsn,'N') = 'C') and (ie_acm_w = 'N') and (ie_se_necessario_w = 'S')) or
           ((coalesce(ie_somente_acmsn,'N') = 'I') and ((ie_acm_w = 'S') or (ie_se_necessario_w = 'S')) and (coalesce(ie_intervalo_hor_w,'S') = 'S')) or (coalesce(ie_somente_acmsn,'N') = 'N'))
        and  ((coalesce(nr_seq_estrut_int::text, '') = '') or ((nr_seq_estrut_int IS NOT NULL AND nr_seq_estrut_int::text <> '') and consistir_se_mat_estrutura(nr_seq_estrut_int,cd_material_w) = 'S'))
        and  ((coalesce(ie_tipo_hemodialise::text, '') = '') or (ie_tipo_hemodialise = ie_tipo_hemodialise_w))
        and  coalesce(ie_tipo_material,ie_tipo_material_w) = ie_tipo_material_w
        and (coalesce(ie_considerar_generico,'N') = 'N' or obter_se_mat_existe_padrao_loc(cd_material_generico_w, cd_local_estoque) = 'S')
        and   ((coalesce(ie_medic_alto_custo,'N') = 'N') or obter_se_mat_alto_custo(cd_material_w, cd_estabelecimento_w) = 'S')
        and  ((coalesce(qt_dose::text, '') = '') or (qt_dose = qt_dose_w))
        and  ((coalesce(qt_volume::text, '') = '') or (to_char(qt_volume) = qt_volume_w)) LIMIT 1;
      end if;
	
      select  count(1)
      into STRICT  nr_regras_w
      from  regra_local_dispensacao where  ie_so_com_estoque = 'S'
      and  ie_saldo_generico = 'S'
      and  cd_estabelecimento = cd_estabelecimento_w LIMIT 1;

      CALL gravar_log_tasy(1812,' nr_prescricao_p=' || nr_prescricao_p || ' cd_estabelecimento_w=' || cd_estabelecimento_w ||
      ' nr_seq_material_p=' || nr_seq_material_p ||
      ' cd_setor_atendimento_w=' || cd_setor_atendimento_w ||
      ' cd_grupo_material_w=' || cd_grupo_material_w ||
      ' cd_subgrupo_w=' || cd_subgrupo_w ||
      ' cd_classe_material_w=' || cd_classe_material_w ||
      ' cd_material_w=' || cd_material_w ||
      ' cd_perfil_p=' ||  cd_perfil_p ||
      ' cd_convenio_w=' || cd_convenio_w ||
      ' ie_motivo_prescricao_w=' ||  ie_motivo_prescricao_w ||
      ' ie_tipo_convenio_w=' ||  ie_tipo_convenio_w ||
      ' nr_seq_forma_chegada_w=' ||  nr_seq_forma_chegada_w ||
      ' ie_tipo_atendimento_w=' ||  ie_tipo_atendimento_w ||
      ' ie_tipo_material_w=' ||  ie_tipo_material_w ||
      ' ie_via_aplicacao_w=' ||  ie_via_aplicacao_w ||
      ' ie_urgencia_w=' || ie_urgencia_w ||
      ' ie_multidose_w=' || ie_multidose_w ||
      ' ie_padronizado_w=' ||  ie_padronizado_w ||
      ' ie_controlado_w=' ||  ie_controlado_w ||
      ' ie_prescr_emergencia_w=' ||  ie_prescr_emergencia_w ||
      ' cd_unidade_medida_w=' ||  cd_unidade_medida_w ||
      ' nr_seq_familia_w=' ||  nr_seq_familia_w ||
      ' dt_liberacao_w=' ||  dt_liberacao_w ||
      ' ie_feriado_w=' ||  ie_feriado_w ||
      ' qt_feriado_w=' ||  qt_feriado_w ||
      ' ie_acm_w=' ||  ie_acm_w ||
      ' ie_se_necessario_w=' || ie_se_necessario_w ||
      ' ie_tipo_hemodialise_w=' ||  ie_tipo_hemodialise_w ||
      ' cd_material_generico_w=' ||  cd_material_generico_w ||
      ' ie_termolabil_w=' ||  ie_termolabil_w ||
      ' qt_total_dispensar_w=' || qt_total_dispensar_w ||
      ' ie_dia_semana_w=' || ie_dia_semana_w ||
      ' ie_gerar_lote_manipulacao_w=' || ie_gerar_lote_manipulacao_w, 'TASYDEFINELOCA');

      if (nr_regras_w > 0) then

        open c03;
        loop
        fetch c03 into
          cd_local_estoque_ww,
          nr_seq_regra_local_ww,
          ie_so_com_estoque_w,
		  ie_saldo_generico_w;
        EXIT WHEN NOT FOUND; /* apply on c03 */
          begin
          if (ie_so_com_estoque_w = 'N') then
				cd_local_estoque_w := cd_local_estoque_ww;
				nr_seq_regra_local_w := nr_seq_regra_local_ww;

		elsif (ie_so_com_estoque_w = 'S') and (obter_saldo_disp_estoque(cd_estabelecimento_w,cd_material_w,cd_local_estoque_ww,clock_timestamp()) >= qt_total_dispensar_w) then
			cd_local_estoque_w := cd_local_estoque_ww;
			nr_seq_regra_local_w := nr_seq_regra_local_ww;

		elsif (ie_saldo_generico_w = 'S') and (obter_saldo_disp_estoque(cd_estabelecimento_w,cd_material_generico_w,cd_local_estoque_ww,clock_timestamp()) >= qt_total_dispensar_w) then
			cd_local_estoque_w := cd_local_estoque_ww;
            nr_seq_regra_local_w := nr_seq_regra_local_ww;
		  end if;
          end;
        end loop;
        close c03;

        select  count(1)
        into STRICT  qt_mat_principal_w
        from  prescr_material where  coalesce(nr_sequencia_diluicao::text, '') = ''
        and  coalesce(nr_seq_kit::text, '') = ''
        and  nr_sequencia = nr_sequencia_w
        and  nr_prescricao = nr_prescricao_p LIMIT 1;

        if (qt_mat_principal_w > 0) and (coalesce(cd_local_estoque_w,0) = 0) then
          cd_local_estoque_w := obter_local_disp_item_generico(nr_prescricao_p,nr_sequencia_w,cd_perfil_p,nm_usuario_p);
        end if;

      end if;

      if (coalesce(cd_local_estoque_w,0) = 0) then

        open c02;
        loop
        fetch c02 into
          cd_local_estoque_ww,
          nr_seq_regra_local_ww,
          ie_so_com_estoque_w,
		  ie_saldo_generico_w;
        EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		if (ie_so_com_estoque_w = 'N') then
            cd_local_estoque_w := cd_local_estoque_ww;
            nr_seq_regra_local_w := nr_seq_regra_local_ww;

		elsif (ie_so_com_estoque_w = 'S') and (obter_saldo_disp_estoque(cd_estabelecimento_w,cd_material_w,cd_local_estoque_ww,clock_timestamp()) >= qt_total_dispensar_w)then
            cd_local_estoque_w := cd_local_estoque_ww;
            nr_seq_regra_local_w := nr_seq_regra_local_ww;

		elsif (ie_saldo_generico_w = 'S') and (obter_saldo_disp_estoque(cd_estabelecimento_w,cd_material_generico_w,cd_local_estoque_ww,clock_timestamp()) >= qt_total_dispensar_w) then
			cd_local_estoque_w := cd_local_estoque_ww;
            nr_seq_regra_local_w := nr_seq_regra_local_ww;
          end if;
          end;
        end loop;
        close c02;

      end if;

      if (coalesce(cd_local_estoque_w,0) = 0) then
        cd_local_estoque_w := cd_loc_estoque_w;
      end if;

      if (cd_local_estoque_w > 0) then

        update  prescr_material
        set  cd_local_estoque   = cd_local_estoque_w,
          nm_usuario      = nm_usuario_p,
          nr_seq_regra_local  = nr_seq_regra_local_w
        where  nr_sequencia  = nr_sequencia_w
        and  nr_prescricao  = nr_prescricao_p
        and  ((nr_seq_recomendacao IS NOT NULL AND nr_seq_recomendacao::text <> '') or (ie_local_estoque_kit_w = 'N') or (coalesce(nr_seq_kit::text, '') = ''))
        and  ((nr_seq_recomendacao IS NOT NULL AND nr_seq_recomendacao::text <> '') or (ie_local_estoque_dil_w = 'N') or (coalesce(nr_sequencia_diluicao::text, '') = ''))
        and  coalesce(cd_motivo_baixa,0)  = 0;

        if (ie_local_estoque_kit_w = 'S') then

		update  prescr_material
		set 	cd_local_estoque  = cd_local_estoque_w,
			nm_usuario    = nm_usuario_p,
			nr_seq_regra_local  = nr_seq_regra_local_w
		where  nr_prescricao    = nr_prescricao_p
		and  	coalesce(cd_motivo_baixa,0)  = 0
		and  	nr_seq_kit    = nr_sequencia_w
		and  	(nr_seq_kit IS NOT NULL AND nr_seq_kit::text <> '')
		and  	coalesce(nr_seq_recomendacao_w::text, '') = '';
        end if;

        if (ie_local_estoque_dil_w = 'S') then
          update  prescr_material
          set  cd_local_estoque  = cd_local_estoque_w,
            nm_usuario    = nm_usuario_p,
            nr_seq_regra_local  = nr_seq_regra_local_w
          where  nr_prescricao    = nr_prescricao_p
          and  coalesce(cd_motivo_baixa,0)  = 0
          and  nr_sequencia_diluicao  = nr_sequencia_w;
        end if;

      end if;
      end;
    end loop;
    close c01;
    end;
  else
    begin
      /* ivan em 25/10/2007 os70266 - incluida verificacao no caso da prescricao nao ter regra de local de estoque */

      if (cd_loc_estoque_w > 0) then
        update  prescr_material
        set    cd_local_estoque = cd_loc_estoque_w,
            nm_usuario  = nm_usuario_p,
            nr_seq_regra_local  = NULL
        where  nr_prescricao  = nr_prescricao_p
        and    coalesce(cd_motivo_baixa,0)  = 0
        and    ((coalesce(nr_seq_material_p,0) = 0) or (nr_sequencia     = nr_seq_material_p) or (nr_sequencia_diluicao  = nr_seq_material_p)or (nr_seq_kit    = nr_seq_material_p))
        and    ((ie_ajusta_lote_proc_etiqueta_w in ('S','N')) or (ie_ajusta_lote_proc_etiqueta_w = 'L' and ie_agrupador not in (16,17)));
      end if;
    end;
  end if;
  end;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

exception
when others then
  ds_erro_w  := substr('ERRO_DEFINE_DISP_PRESCR: '||' sqlerrm ' || sqlerrm(SQLSTATE) ||' STACK ' || dbms_utility.format_call_stack || ' - ' || nr_prescricao_p || ' - ' || nr_seq_material_p || ' - ' || cd_perfil_p,1,3999);
  CALL gravar_log_tasy(31, ds_erro_w, nm_usuario_p);
end;

end;
$body$
LANGUAGE PLPGSQL
