CREATE OR REPLACE PROCEDURE define_periodo_conta ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, dt_periodo_inicial_p INOUT timestamp, dt_periodo_final_p INOUT timestamp) AS $body$
DECLARE


dt_periodo_incial_w	        timestamp;
dt_alta_w		        timestamp;
dt_periodo_final_w	        timestamp;
cd_convenio_w		        bigint;
ie_periodo_inicial_seg_w        convenio_estabelecimento.ie_periodo_inicial_seg%type;


BEGIN

select	dt_alta,
	cd_convenio
into STRICT	dt_alta_w,
	cd_convenio_w
from	atend_categoria_convenio a,
	atendimento_paciente b
where	a.nr_atendimento	= b.nr_atendimento
and	b.nr_atendimento	= nr_atendimento_p
and	a.nr_seq_interno 	= obter_atecaco_atendimento(b.nr_atendimento);

select  coalesce(max(ie_periodo_inicial_seg),'N')
into STRICT    ie_periodo_inicial_seg_w
from    convenio_estabelecimento
where   cd_convenio = cd_convenio_w
and     cd_estabelecimento = cd_estabelecimento_p;

if (ie_periodo_inicial_seg_w = 'S') then
        begin

        select  max(dt_periodo_final) + 1/86400 dt_periodo_final 
        into STRICT    dt_periodo_incial_w
        from    conta_paciente
        where   nr_atendimento = nr_atendimento_p;

        end;
else
        begin

        select	max(dt_periodo_final)
        into STRICT	dt_periodo_incial_w
        from	conta_paciente
        where	nr_atendimento		= nr_atendimento_p;

        end;
end if;

if (coalesce(dt_periodo_incial_w::text, '') = '') then
	select	dt_entrada
	into STRICT	dt_periodo_incial_w
	from	atendimento_paciente
	where	nr_atendimento		= nr_atendimento_p;	

end if;


dt_periodo_final_w	:= Obter_Data_Final_Conta(dt_periodo_incial_w,dt_alta_w,cd_convenio_w,cd_estabelecimento_p);

dt_periodo_inicial_p	:= dt_periodo_incial_w;
dt_periodo_final_p	:= dt_periodo_final_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
