SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE define_prioridade_atend (nr_atendimento_p bigint, ie_tipo_atendimento_p bigint) AS $body$
DECLARE

 
nr_seq_triagem_classif_w	bigint;
nr_seq_triagem_w			bigint;
nr_seq_triagem_prioridade_w	bigint;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_tipo_atendimento_p IS NOT NULL AND ie_tipo_atendimento_p::text <> '') then 
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_triagem_classif_w 
	from	regra_triagem_classif 
	where	ie_tipo_atendimento = ie_tipo_atendimento_p 
	and		ie_situacao = 'A';
 
	if (nr_seq_triagem_classif_w IS NOT NULL AND nr_seq_triagem_classif_w::text <> '') then 
		select	max(nr_seq_triagem), 
				max(nr_seq_triagem_prioridade) 
		into STRICT	nr_seq_triagem_w, 
				nr_seq_triagem_prioridade_w 
		from	regra_triagem_classif 
		where	nr_sequencia = nr_seq_triagem_classif_w 
		and		ie_situacao = 'A';
		 
		update	atendimento_paciente 
		set		nr_seq_triagem = nr_seq_triagem_w, 
				nr_seq_triagem_prioridade = nr_seq_triagem_prioridade_w 
		where	nr_atendimento = nr_atendimento_p;		
	end if;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
