CREATE OR REPLACE PROCEDURE definir_acomodacao_paciente ( ie_acomod_todos_pac_p text, ie_registra_chegada_p text, ie_acomodacao_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_paciente_w	bigint;
dt_prevista_w		timestamp;
nr_seq_atendimento_w	bigint;


BEGIN 
 
select	max(nr_seq_paciente), 
	max(dt_prevista), 
	max(nr_seq_atendimento) 
into STRICT	nr_seq_paciente_w, 
	dt_prevista_w, 
	nr_seq_atendimento_w 
FROM  	paciente_atendimento 
WHERE 	nr_atendimento 		= nr_atendimento_p 
AND  	TRUNC(dt_prevista)	= TRUNC(clock_timestamp()) 
AND  	coalesce(dt_cancelamento::text, '') = '';
 
if ( ie_acomod_todos_pac_p = 'S') then 
	begin 
	if (ie_registra_chegada_p = 'S') then 
		update	paciente_atendimento 
		set 	nr_seq_local 		= ie_acomodacao_p, 
			dt_acomodacao_paciente 	= clock_timestamp(), 
			nm_usuario 		= nm_usuario_p, 
			dt_chegada 		= coalesce(dt_chegada,clock_timestamp()) 
		where 	obter_dados_paciente_setor(nr_seq_paciente, 'C') = obter_dados_paciente_setor(nr_seq_paciente_w ,'C')	 
		and  	to_char(dt_prevista,'dd/mm/yyyy') = dt_prevista_w 
		and  	(dt_chegada IS NOT NULL AND dt_chegada::text <> '');
	else	 
		update	paciente_atendimento 
		set 	nr_seq_local 		= ie_acomodacao_p, 
			dt_acomodacao_paciente 	= clock_timestamp(), 
			nm_usuario 		= nm_usuario_p 
		where 	obter_dados_paciente_setor(nr_seq_paciente, 'C') = obter_dados_paciente_setor(nr_seq_paciente_w ,'C')	 
		and  	trunc(dt_prevista) = trunc(dt_prevista_w);	
	end if;
	end;
else 
	update	paciente_atendimento 
	set 	nr_seq_local		= ie_acomodacao_p, 
		dt_chegada 		= coalesce(dt_chegada,clock_timestamp()), 
		dt_acomodacao_paciente 	= clock_timestamp(), 
		nm_usuario 		= nm_usuario_p 
	where 	nr_seq_atendimento 	= nr_seq_atendimento_w;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
