CREATE OR REPLACE PROCEDURE definir_autor_origem_conv ( nr_seq_autor_orig_p bigint, cd_autorizacao_p text, cd_senha_p text, nr_seq_estagio_p bigint, nr_sequencia_p bigint, nr_seq_estagio_filtro_p bigint, dt_envio_p timestamp, dt_retorno_p timestamp, dt_inicio_vigencia_p timestamp, dt_autorizacao_p timestamp, dt_fim_vigencia_p timestamp, ie_tipo_guia_p text ) AS $body$
