        dt_envio       		= dt_envio_p,
        dt_retorno     		= coalesce(dt_retorno_p,dt_retorno),
        dt_inicio_vigencia 	= dt_inicio_vigencia_p,
        dt_fim_vigencia    	= dt_fim_vigencia_p,
	ie_tipo_guia		= ie_tipo_guia_p,
