CREATE OR REPLACE PROCEDURE definir_bens_tranferencia ( nr_sequencia_p bigint, nr_seq_regra_p bigint, nr_seq_bem_p text, nr_seq_tipo_p bigint, nr_seq_local_p bigint, nr_seq_marca_p bigint, cd_estabelecimento_p bigint, ie_imobilizado_p text, ie_situacao_p text, nr_seq_regra_Conta_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	pat_bem a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.cd_bem			= coalesce(nr_seq_bem_p, a.cd_bem)
and	a.nr_sequencia		= coalesce(nr_sequencia_p, a.nr_sequencia)
and	a.nr_seq_tipo		= coalesce(nr_seq_tipo_p, a.nr_seq_tipo)
and	a.nr_seq_local		= coalesce(nr_seq_local_p, a.nr_seq_local)
and	a.ie_imobilizado		= coalesce(ie_imobilizado_p, a.ie_imobilizado)
and	a.ie_situacao		= coalesce(ie_situacao_p, a.ie_situacao)
and (a.nr_seq_marca		= coalesce(nr_seq_marca_p, a.nr_seq_marca) or coalesce(nr_seq_marca_p::text, '') = '')
and	a.nr_seq_regra_conta	= coalesce(nr_seq_regra_conta_p, a.nr_seq_regra_conta);


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into pat_transf_bem(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_doc_transf,
		nr_seq_bem)
	values (	nextval('pat_transf_bem_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_regra_p,
		nr_sequencia_w);
	end;
end loop;
close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
