SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_consiste_atend_prescr ( qt_horas_futuras_p bigint, dt_prescricao_p timestamp, nm_computador_p text, nr_atendimento_selec_p bigint, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nr_prescricao_p bigint, ie_define_setor_atend_p text, ie_pac_usu_p text, ie_setores_usuario_p text, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w			integer;
ie_setor_atendimento_w			varchar(1);
ie_setor_mae_w					setor_atendimento.ie_rn_mae%type;
cd_setor_atendimento_mae_w		setor_atendimento.cd_setor_atendimento%type;
nr_atendimento_mae_w			atendimento_paciente.nr_atendimento%type;
cd_setor_atendimento_filho_w	setor_atendimento.cd_setor_atendimento%type;
		

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_atendimento_selec_p IS NOT NULL AND nr_atendimento_selec_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	
	select 	coalesce(max(nr_atendimento_mae),0)
	into STRICT	nr_atendimento_mae_w
	from 	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;
	
	CALL consistir_data_prescricao(qt_horas_futuras_p,dt_prescricao_p);
	
	select	coalesce(max('S'), 'N')
	into STRICT	ie_setor_atendimento_w
	from	setor_atendimento a
	where	a.cd_setor_atendimento in (
			SELECT	b.cd_setor_atendimento
			from	atend_paciente_unidade b
			where	b.nr_atendimento = nr_atendimento_p
			and	b.cd_setor_atendimento = cd_setor_atendimento_p);
	
	if (ie_define_setor_atend_p = 'S') then
		begin
		
		if (ie_pac_usu_p = 'B') then
			begin
				cd_setor_atendimento_w := null;	
			end;
		elsif (ie_pac_usu_p = 'C') then
			begin
			
			select	max(cd_setor_atendimento)
			into STRICT	cd_setor_atendimento_w
			from	computador
			where	nm_computador_pesquisa 	= padronizar_nome(upper(nm_computador_p));
			end;
		elsif (ie_pac_usu_p = 'R') then
			begin
			cd_setor_atendimento_w := (obter_setor_prescr_regra(cd_perfil_p))::numeric;
			end;
		elsif (ie_pac_usu_p = 'U') and (ie_setores_usuario_p = 'S') then
			begin
			
			cd_setor_atendimento_w	:= cd_setor_atendimento_p;
			end;
			
		elsif (ie_pac_usu_p = 'U') and (nr_atendimento_selec_p > 0) and (ie_setor_atendimento_w = 'S') then
			begin
			cd_setor_atendimento_w	:= cd_setor_atendimento_p;
			end;
		elsif (ie_pac_usu_p = 'D') then
			begin
				if (nr_atendimento_mae_w > 0) then
					begin
						select 	coalesce(max(ie_rn_mae),'N'),
								coalesce(max(cd_setor_atendimento),0)
						into STRICT 	ie_setor_mae_w, 
								cd_setor_atendimento_mae_w
						from 	setor_atendimento
						where 	cd_setor_atendimento = obter_unidade_atendimento(nr_atendimento_mae_w,'A','CS');
				
						select	coalesce(max(a.cd_setor_atendimento),0)
						into STRICT 	cd_setor_atendimento_filho_w
						from 	unidade_atendimento a
						where 	a.nr_seq_interno = (SELECT coalesce(max(b.nr_seq_unidade_rn),0) from setor_atendimento b
													where b.cd_setor_atendimento = obter_unidade_atendimento(nr_atendimento_p,'A','CS'));
													
						if (ie_setor_mae_w = 'S') and (cd_setor_atendimento_mae_w = cd_setor_atendimento_filho_w) then
							cd_setor_atendimento_w := cd_setor_atendimento_mae_w;
						else
							cd_setor_atendimento_w := coalesce(obter_unidade_atendimento(nr_atendimento_p,'A','CS'), 0);
						end if;
					end;	
				end if;	
			end;
		elsif (nr_atendimento_selec_p > 0) then
			begin
			
			select	coalesce(obter_unidade_atendimento(nr_atendimento_selec_p,'IA','CS'),obter_unidade_atendimento(nr_atendimento_selec_p,'A','CS'))
			into STRICT	cd_setor_atendimento_w
			;
			end;
		end if;
		end;
	end if;

	CALL definir_atendimento_prescricao(
		cd_setor_atendimento_w,
		nr_prescricao_p,
		nr_atendimento_selec_p,
		nm_usuario_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE definir_consiste_atend_prescr ( qt_horas_futuras_p bigint, dt_prescricao_p timestamp, nm_computador_p text, nr_atendimento_selec_p bigint, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nr_prescricao_p bigint, ie_define_setor_atend_p text, ie_pac_usu_p text, ie_setores_usuario_p text, cd_perfil_p bigint, nm_usuario_p text) FROM PUBLIC;

