CREATE OR REPLACE PROCEDURE definir_conta_padrao_pessoa (cd_pessoa_fisica_p text, cd_cgc_p text, cd_banco_p bigint, cd_agencia_bancaria_p text, nr_conta_p text, nm_usuario_p text) AS $body$
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	update	pessoa_fisica_conta
	set	ie_conta_pagamento	= 'S',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_banco		= cd_banco_p
	and	cd_agencia_bancaria	= cd_agencia_bancaria_p
	and	nr_conta		= nr_conta_p;

	update	pessoa_fisica_conta
	set	ie_conta_pagamento	= 'N',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and (cd_banco		<> cd_banco_p or
		cd_agencia_bancaria	<> cd_agencia_bancaria_p or
		nr_conta		<> nr_conta_p);

elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	update	pessoa_juridica_conta
	set	ie_conta_pagamento	= 'S',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_cgc			= cd_cgc_p
	and	cd_banco		= cd_banco_p
	and	cd_agencia_bancaria	= cd_agencia_bancaria_p
	and	nr_conta		= nr_conta_p;

	update	pessoa_juridica_conta
	set	ie_conta_pagamento	= 'N',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_cgc			= cd_cgc_p
	and (cd_banco		<> cd_banco_p or
		cd_agencia_bancaria	<> cd_agencia_bancaria_p or
		nr_conta		<> nr_conta_p);

end if;

commit;

end;
$body$
