CREATE OR REPLACE PROCEDURE definir_data_fim_hist_saude ( nr_sequencia_p bigint, dt_fim_p timestamp, ds_obs_p text, ie_nao_sabe_fim_p text, ie_tabela_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (ie_tabela_p = 'PAV') then 
	update	paciente_habito_vicio 
	set   dt_fim		= dt_fim_p, 
		ds_observacao  = substr(substr(ds_observacao,1,3500)||CASE WHEN ds_observacao = NULL THEN Null  ELSE chr(13)||chr(10) END ||ds_obs_p,1,255), 
		nm_usuario	= nm_usuario_p 
	where  nr_sequencia  	= nr_sequencia_p;
elsif (ie_tabela_p = 'MEU') then 
	update paciente_medic_uso     
    set   dt_fim		= coalesce(dt_fim_p,dt_fim), 
		ds_observacao 	= substr(substr(ds_observacao,1,3500)||CASE WHEN ds_observacao = NULL THEN Null  ELSE chr(13)||chr(10) END ||ds_obs_p,1,255), 
		ie_nao_sabe_fim	= coalesce(ie_nao_sabe_fim_p,ie_nao_sabe_fim), 
		nm_usuario	= nm_usuario_p 
    where  nr_sequencia  	= nr_sequencia_p;
elsif (ie_tabela_p = 'DPA') then 
	update paciente_antec_clinico   
	set   dt_fim     	= dt_fim_p,  
		ds_observacao	= substr(substr(ds_observacao,1,3500)||CASE WHEN ds_observacao = NULL THEN Null  ELSE chr(13)||chr(10) END ||ds_obs_p,1,255), 
		nm_usuario	= nm_usuario_p 
	where  nr_sequencia  	= nr_sequencia_p;
elsif (ie_tabela_p = 'ARA') then 
	update paciente_alergia 
	set   dt_fim		= dt_fim_p, 
		ds_observacao  = substr(substr(ds_observacao,1,3500)||CASE WHEN ds_observacao = NULL THEN Null  ELSE chr(13)||chr(10) END ||ds_obs_p,1,255), 
		nm_usuario	= nm_usuario_p 
	where  nr_sequencia  	= nr_sequencia_p;
end if;
 
commit;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
