CREATE OR REPLACE PROCEDURE definir_desconto_ret_glosa (nr_seq_ret_glosa_p bigint, vl_desconto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ret_item_w	bigint;
vl_desconto_total_w	double precision;


BEGIN

select	max(nr_seq_ret_item)
into STRICT	nr_seq_ret_item_w
from	convenio_retorno_glosa
where	nr_sequencia		= nr_seq_ret_glosa_p;

update	convenio_retorno_glosa
set	vl_desconto_item	= vl_desconto_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_ret_glosa_p;

select	coalesce(sum(vl_desconto_item),0)
into STRICT	vl_desconto_total_w
from	convenio_retorno_glosa
where	nr_seq_ret_item		= nr_seq_ret_item_w;


update	convenio_retorno_item
set	vl_desconto	= vl_desconto_total_w,
	vl_pago		= CASE WHEN vl_pago=0 THEN vl_pago  ELSE vl_pago - vl_desconto_total_w END ,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_ret_item_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
