SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_dispensacao_diluicao ( nr_prescricao_p bigint, nr_seq_matmed_p bigint, ie_regra_disp_p text, nm_usuario_p text) AS $body$
DECLARE


cd_estab_prescr_w	smallint;
cd_motivo_baixa_w	smallint;
nr_seq_material_w	integer;

c01 CURSOR FOR
SELECT	nr_sequencia
from	prescr_material
where	nr_prescricao		= nr_prescricao_p
and	nr_sequencia_diluicao	= nr_seq_matmed_p
and	ie_agrupador		= 3;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_matmed_p IS NOT NULL AND nr_seq_matmed_p::text <> '') and (ie_regra_disp_p IS NOT NULL AND ie_regra_disp_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	cd_estabelecimento
	into STRICT	cd_estab_prescr_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select	max(obter_valor_param_usuario(924, 194, obter_perfil_ativo, nm_usuario_p, cd_estab_prescr_w))
	into STRICT	cd_motivo_baixa_w
	;

	open c01;
	loop
	fetch c01 into nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update	prescr_material
		set	ie_regra_disp	= CASE WHEN coalesce(ie_regra_disp,'X')='D' THEN  ie_regra_disp  ELSE ie_regra_disp_p END ,
			dt_baixa	= CASE WHEN ie_regra_disp_p='N' THEN  clock_timestamp()  ELSE null END ,
			cd_motivo_baixa	= CASE WHEN ie_regra_disp_p='N' THEN  cd_motivo_baixa_w  ELSE 0 END
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_material_w;

		end;
	end loop;
	close c01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
