SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_dispensacao_dil_html5 ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_cpoe_p cpoe_material.nr_sequencia%type, ie_regra_disp_p prescr_material.ie_regra_disp%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_motivo_baixa_w			prescr_material.cd_motivo_baixa%type;
nr_prescricao_vigente_w		prescr_medica.nr_prescricao%type;
				
c01 CURSOR FOR
SELECT	a.nr_sequencia,
		a.nr_prescricao
from	prescr_material a
where	a.nr_prescricao >= nr_prescricao_vigente_w
and		a.nr_seq_mat_cpoe = nr_seq_cpoe_p
and		a.ie_agrupador = 3;

BEGIN

nr_prescricao_vigente_w := gpt_obter_prescricao_vigente(nr_seq_cpoe_p, 'M');

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and (ie_regra_disp_p IS NOT NULL AND ie_regra_disp_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	max(obter_valor_param_usuario(924, 194, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo))
	into STRICT	cd_motivo_baixa_w
	;
	
	for r_c01_w in c01
	loop
		begin
		
		update	prescr_material a
		set		a.ie_regra_disp = CASE WHEN coalesce(a.ie_regra_disp, 'X')='D' THEN  ie_regra_disp  ELSE ie_regra_disp_p END ,
				a.dt_baixa = CASE WHEN ie_regra_disp_p='N' THEN  clock_timestamp()  ELSE null END ,
				a.cd_motivo_baixa = CASE WHEN ie_regra_disp_p='N' THEN  cd_motivo_baixa_w  ELSE 0 END
		where	a.nr_prescricao = r_c01_w.nr_prescricao
		and		a.nr_sequencia = r_c01_w.nr_sequencia;
		
		end;
	end loop;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
