SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_dispensacao_farm_html5 ( nr_prescricao_p bigint, nr_seq_item_cpoe_p bigint, nm_tabela_p text, ie_regra_disp_p text, ds_motivo_disp_p text, nr_seq_motivo_disp_p bigint, nm_usuario_p text) AS $body$
DECLARE


param01_w  varchar(1);

c01 CURSOR(param01_pc text) FOR						
SELECT	
	a.nr_sequencia nr_seq_material,
	a.nr_prescricao,
	a.ie_agrupador
from	prescr_material a,
		prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and		((param01_pc = 'E') or (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''))
and		coalesce(b.dt_liberacao_farmacia::text, '') = ''
and		coalesce(b.dt_suspensao::text, '') = ''
and (a.nr_seq_mat_cpoe = nr_seq_item_cpoe_p and a.ie_agrupador not in (8, 12)
		or a.nr_seq_dieta_cpoe = nr_seq_item_cpoe_p and a.ie_agrupador in (8, 12));

BEGIN

    param01_w := Obter_Param_Usuario(252, 1, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, param01_w);

	for r_c01_w in c01(param01_w)
	loop
		if ((nm_tabela_p = 'CPOE_MATERIAL' and r_c01_w.ie_agrupador in (1, 4, 5))
			or (nm_tabela_p = 'CPOE_DIETA' and r_c01_w.ie_agrupador in (8, 12))) then
			CALL definir_dispensacao_farmacia(r_c01_w.nr_prescricao, r_c01_w.nr_seq_material, ie_regra_disp_p, ds_motivo_disp_p, nr_seq_motivo_disp_p, nm_usuario_p);
		end if;
	end loop;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
