SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_dispensacao_kit ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_necessita_disp_p text, nm_usuario_p text) AS $body$
DECLARE


cd_estab_prescr_w	smallint;
cd_motivo_baixa_w	smallint;


BEGIN

if (nr_prescricao_p > 0) and (nr_sequencia_p > 0) then

	select	cd_estabelecimento
	into STRICT	cd_estab_prescr_w
	from	prescr_medica
	where	nr_prescricao 		= 	nr_prescricao_p;

	select	max(obter_valor_param_usuario(924, 194, obter_perfil_ativo, nm_usuario_p, cd_estab_prescr_w))
	into STRICT	cd_motivo_baixa_w
	;

	update	prescr_material
	set	ie_necessita_disp_kit	=	ie_necessita_disp_p
	where	nr_prescricao		=	nr_prescricao_p
	and	nr_sequencia		=	nr_sequencia_p;

	update	prescr_material
	set	ie_regra_disp		= 	ie_necessita_disp_p,
		dt_baixa		= 	CASE WHEN ie_necessita_disp_p='N' THEN  clock_timestamp()  ELSE null END ,
		cd_motivo_baixa		= 	CASE WHEN ie_necessita_disp_p='N' THEN  cd_motivo_baixa_w  ELSE 0 END
	where	nr_prescricao		= 	nr_prescricao_p
	and	nr_seq_kit		= 	nr_sequencia_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE definir_dispensacao_kit ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_necessita_disp_p text, nm_usuario_p text) FROM PUBLIC;

