CREATE OR REPLACE PROCEDURE definir_dt_fim_alergia ( nr_seq_pac_alergia_p bigint, dt_fim_p timestamp, ds_observacao_p text) AS $body$
DECLARE

 
ds_observacao_w		varchar(8000);
ds_observacao_ww	varchar(4000);


BEGIN 
 
select	max(ds_observacao) 
into STRICT  ds_observacao_w 
from	 paciente_alergia 
where  nr_sequencia 	= nr_seq_pac_alergia_p;
 
if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then 
 ds_observacao_w := ds_observacao_w || chr(13) || chr(10);
end if;
 
ds_observacao_ww := substr(ds_observacao_w || ds_observacao_p, 1, 4000);
 
update	paciente_alergia 
set	dt_fim		= dt_fim_p, 
	ds_observacao	= ds_observacao_ww 
where	nr_sequencia	= nr_seq_pac_alergia_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
