CREATE OR REPLACE PROCEDURE definir_equip_estrut_proc (cd_estrutura_p bigint, ie_estrutura_p text, cd_equipamento_p bigint, ie_tipo_equipamento_p text, nm_usuario_p text) AS $body$
DECLARE


cd_procedimento_w	procedimento.cd_procedimento%type;
ie_origem_proced_w	procedimento.ie_origem_proced%type;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	estrutura_procedimento_v
	where	((ie_estrutura_p = 'A' AND cd_area_procedimento = cd_estrutura_p) or
		 (ie_estrutura_p = 'E' AND cd_especialidade = cd_estrutura_p) or
		 (ie_estrutura_p = 'G' AND cd_grupo_proc = cd_estrutura_p))
	order by cd_procedimento,
		 ie_origem_proced;


BEGIN

open C01;
loop
fetch C01 into
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert	into procedimento_equip(
		CD_EQUIPAMENTO,
		CD_PROCEDIMENTO,
		DT_ATUALIZACAO,
		IE_ORIGEM_PROCED,
		IE_TIPO_EQUIPAMENTO,
		NM_USUARIO,
		NR_SEQUENCIA)
	values (cd_equipamento_p,
		cd_procedimento_w,
		clock_timestamp(),
		ie_origem_proced_w,
		ie_tipo_equipamento_p,
		nm_usuario_p,
		nextval('procedimento_equip_seq'));

	end;
end loop;
close C01;

commit;

end;
$body$
