CREATE OR REPLACE PROCEDURE definir_fim_amamentacao_hist ( nr_sequencia_p bigint, dt_fim_p timestamp, ds_observacao_p text, cd_pessoa_fisica_p text, ie_finalizar_todos_p text) AS $body$
DECLARE


nr_sequencia_w		historico_saude_mulher.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	historico_saude_mulher
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(dt_inicio_amamentacao IS NOT NULL AND dt_inicio_amamentacao::text <> '')
	and	coalesce(dt_fim_amamentacao::text, '') = ''
	and	coalesce(dt_inativacao::text, '') = '';


BEGIN

	if (ie_finalizar_todos_p = 'S') then

		open C01;
		loop
		fetch C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

				update 	historico_saude_mulher
				set 	dt_fim_amamentacao 	= dt_fim_p,
					ds_observacao		= substr(ds_observacao||CASE WHEN ds_observacao = NULL THEN Null  ELSE chr(13)||chr(10) END ||ds_observacao_p,1,255)
				where 	nr_sequencia 		= nr_sequencia_w;

			end;
		end loop;
		close C01;

	else

		update 	historico_saude_mulher
		set 	dt_fim_amamentacao  	= dt_fim_p,
			ds_observacao		= substr(ds_observacao||CASE WHEN ds_observacao = NULL THEN Null  ELSE chr(13)||chr(10) END ||ds_observacao_p,1,255)
		where 	nr_sequencia 		= nr_sequencia_p;

	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
