CREATE OR REPLACE PROCEDURE definir_forma_pagto_cheque ( nr_seq_cheque_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_contabil_w		timestamp;
ie_forma_pagto_w	varchar(2);


BEGIN

select	max(a.dt_contabil)
into STRICT	dt_contabil_w
from	cheque_cr a
where	a.nr_seq_cheque	= nr_seq_cheque_p;

if (trunc(dt_contabil_w,'dd') = trunc(dt_referencia_p,'dd')) then
	ie_forma_pagto_w	:= 'AV';
else
	ie_forma_pagto_w	:= 'PD';
end if;

update	cheque_cr
set	ie_forma_pagto	= ie_forma_pagto_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_seq_cheque	= nr_seq_cheque_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
