CREATE OR REPLACE PROCEDURE definir_hora_pagamento ( nr_seq_escrit_p text, nr_titulo_p text, dt_hora_p text) AS $body$
BEGIN

if (dt_hora_p IS NOT NULL AND dt_hora_p::text <> '') and (( length(dt_hora_p) < 4) or ( substr(dt_hora_p,1,2) >= 24 ) or ( substr(dt_hora_p,3,4) >= 60 )) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(449232);
end if;


update titulo_pagar_escrit
set hr_pagamento = dt_hora_p
where nr_seq_escrit = nr_seq_escrit_p
and nr_titulo = nr_titulo_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
