SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_inventario_contagem ( nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_inventario_p bigint, nr_seq_item_p bigint, ie_coluna_contagem_p text, ie_estoque_lote_p text) AS $body$
DECLARE


cd_material_w			bigint;
qt_inventario_w			double precision;
qt_inventario_lote_w	inventario_material_lote.qt_inventario%type;
ie_estoque_lote_w		material_estab.ie_estoque_lote%type;
nr_sequencia_w			bigint;
ie_define_invetario_w	varchar(1) := 'S';

c01 CURSOR FOR
SELECT	nr_sequencia,
	CASE WHEN ie_coluna_contagem_p='QT_CONTAGEM' THEN  qt_contagem WHEN ie_coluna_contagem_p='QT_RECONTAGEM' THEN  qt_recontagem WHEN ie_coluna_contagem_p='QT_SEG_RECONTAGEM' THEN  qt_seg_recontagem WHEN ie_coluna_contagem_p='QT_TERC_RECONTAGEM' THEN  qt_terc_recontagem END
from	inventario_material_lote
where	nr_seq_inventario 	= nr_seq_inventario_p
and	nr_seq_item 		= nr_seq_item_p;


BEGIN

select	CASE WHEN ie_coluna_contagem_p='QT_CONTAGEM' THEN  qt_contagem WHEN ie_coluna_contagem_p='QT_RECONTAGEM' THEN  qt_recontagem WHEN ie_coluna_contagem_p='QT_SEG_RECONTAGEM' THEN  qt_seg_recontagem WHEN ie_coluna_contagem_p='QT_TERC_RECONTAGEM' THEN  qt_terc_recontagem END ,
		obter_se_material_estoque_lote(cd_estabelecimento_p, cd_material)
into STRICT	qt_inventario_w,
		ie_estoque_lote_w
from	inventario_material
where	nr_seq_inventario = nr_seq_inventario_p
and	nr_sequencia = nr_seq_item_p;

if (coalesce(ie_estoque_lote_w,'N') = 'S') then
	begin
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w,
		qt_inventario_lote_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (qt_inventario_lote_w IS NOT NULL AND qt_inventario_lote_w::text <> '') then
			update	inventario_material_lote
			set	qt_inventario 		= qt_inventario_lote_w,
				qt_diferenca		= qt_inventario_lote_w - qt_saldo
			where	nr_sequencia 		= nr_sequencia_w;
		else
			ie_define_invetario_w := 'N';
		end if;

		end;
	end loop;
	close c01;
	
	if (ie_define_invetario_w = 'S') then
		select	sum(qt_inventario)
		into STRICT	qt_inventario_w
		from	inventario_material_lote
		where	nr_seq_item = nr_seq_item_p;
	else
		qt_inventario_w := null;
	end if;
	end;
end if;

if (qt_inventario_w IS NOT NULL AND qt_inventario_w::text <> '') then
	update	inventario_material
	set	qt_inventario 	= qt_inventario_w,
		qt_diferenca	= qt_inventario_w - qt_saldo
	where	nr_seq_inventario = nr_seq_inventario_p
	and	nr_sequencia = nr_seq_item_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
