CREATE OR REPLACE PROCEDURE definir_item_superior ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, ie_opcao_p text) AS $body$
DECLARE


/*	ie_opcao_p
	S - definir
	N - desfazer */
BEGIN

if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and
	((ie_opcao_p = 'S') or (ie_opcao_p = 'N')) then
	begin
	update	paciente_atend_medic
	set	ie_item_superior		= ie_opcao_p
	where	nr_seq_atendimento	= nr_seq_atendimento_p
	and	nr_seq_material		= nr_seq_material_p;
	end;
end if;

commit;

end;
