CREATE OR REPLACE PROCEDURE definir_itens_marca ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, nr_seq_familia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w			integer;


c01 CURSOR FOR
SELECT	distinct(a.cd_material)
from	estrutura_material_v a
where	a.cd_classe_material	= coalesce(cd_classe_material_p, a.cd_classe_material)
and	a.cd_grupo_material	= coalesce(cd_grupo_material_p, a.cd_grupo_material)
and	a.cd_subgrupo_material	= coalesce(cd_subgrupo_material_p, a.cd_subgrupo_material)
and (a.nr_seq_familia		= nr_seq_familia_p or coalesce(nr_seq_familia_p, 0) = 0)
and	a.ie_situacao		= 'A'
and not exists (
	SELECT	1
	from	material_marca b
	where	b.nr_sequencia	= nr_sequencia_p
	and	a.cd_material	= b.cd_material);


BEGIN

open c01;
loop
fetch c01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	insert into material_marca(
		nr_sequencia,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		qt_prioridade,
		ie_situacao,
		cd_estabelecimento)
	values (	nr_sequencia_p,
		cd_material_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		10,
		'A',
		cd_estabelecimento_p);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
