CREATE OR REPLACE PROCEDURE definir_itens_protocolo_solic ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, ie_tipo_material_p text, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, nr_seq_familia_p bigint, ie_padronizado_p text, ie_mat_estoque_p text, nm_usuario_p text) AS $body$
DECLARE



cd_material_w			material.cd_material%type;


c01 CURSOR FOR
SELECT	b.cd_material
from	estrutura_material_v a,
	material b
where	a.cd_material		= b.cd_material
and	b.ie_situacao = 'A'
and	a.cd_classe_material	= coalesce(cd_classe_material_p, a.cd_classe_material)
and	a.cd_grupo_material	= coalesce(cd_grupo_material_p, a.cd_grupo_material)
and	a.cd_subgrupo_material	= coalesce(cd_subgrupo_material_p, a.cd_subgrupo_material)
and	b.ie_tipo_material	= coalesce(ie_tipo_material_p, b.ie_tipo_material)
and	((coalesce(nr_seq_familia_p, 0) = 0) or (b.nr_seq_familia = nr_seq_familia_p))
and	((ie_mat_estoque_p = '2') or
	((ie_mat_estoque_p = '0') and (obter_se_material_estoque(cd_estabelecimento_p, 0, b.cd_material) = 'S')) or
	((ie_mat_estoque_p = '1') and (obter_se_material_estoque(cd_estabelecimento_p, 0, b.cd_material) = 'N')))
and	((ie_padronizado_p = '2') or
	((ie_padronizado_p = '0') and (obter_se_material_padronizado(cd_estabelecimento_p, b.cd_material) = 'S')) or
	((ie_padronizado_p = '1') and (obter_se_material_padronizado(cd_estabelecimento_p, b.cd_material) = 'N')))
and not exists (
	SELECT	1
	from	prot_solic_compra_item x
	where	x.nr_seq_protocolo = nr_sequencia_p
	and	x.cd_material = b.cd_material);



BEGIN

open c01;
loop
fetch c01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into prot_solic_compra_item(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_protocolo,
		cd_material,
		qt_material)
	values (	nextval('prot_solic_compra_item_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		cd_material_w,
		0);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
