CREATE OR REPLACE PROCEDURE definir_kit_agenda ( nr_seq_agenda_p bigint, cd_kit_material_p bigint) AS $body$
DECLARE


ds_kit_marcado_agenda_w	varchar(255);


BEGIN

if (coalesce(cd_kit_material_p,0) > 0) and (coalesce(nr_seq_agenda_p,0) > 0) then
	select	max(ds_kit_marcado_agenda)
	into STRICT	ds_kit_marcado_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (coalesce(ds_kit_marcado_agenda_w::text, '') = '') then
		ds_kit_marcado_agenda_w := cd_kit_material_p;
	else
		ds_kit_marcado_agenda_w := ds_kit_marcado_agenda_w || ',' ||cd_kit_material_p;
	end if;
	update	agenda_paciente
	set	ds_kit_marcado_agenda 	= ds_kit_marcado_agenda_w
	where	nr_sequencia		= nr_seq_agenda_p;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
