CREATE OR REPLACE PROCEDURE definir_medico_seg_aprov ( nr_seq_laudo_p bigint, cd_resp_seg_aprov_p text, nm_usuario_p text) AS $body$
BEGIN

if (Obter_se_medico(cd_resp_seg_aprov_p, 'M') <> 'S') then
	--O código informado não pertence a um médico.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(210845);
end if;

update	laudo_paciente
set	cd_resp_seg_aprov = cd_resp_seg_aprov_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario     = nm_usuario_p
where	nr_sequencia   = nr_seq_laudo_p;

commit;

end;
$body$
