CREATE OR REPLACE PROCEDURE definir_necessidade_treinam (nr_sequencia_p bigint, ie_necessario_p text, nr_seq_motivo_p bigint default null) AS $body$
BEGIN
	
if (coalesce(nr_sequencia_p,0) > 0) then
	update	qms_treinamento
	set	ie_necessario = ie_necessario_p,
		nr_seq_mot_nao_aplic = nr_seq_motivo_p
	where	nr_sequencia = nr_sequencia_p
	and	coalesce(ie_situacao, 'A') = 'A';
	
	commit;
end if;

end;
$body$
