CREATE OR REPLACE PROCEDURE definir_perc_nf_credito_item ( nr_seq_nf_credito_p bigint, pr_valor_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_nf_credito_item_w	nf_credito_item.nr_sequencia%type;

c01 CURSOR FOR
SELECT	nr_sequencia
from	nf_credito_item
where	nr_seq_nf_credito = nr_seq_nf_credito_p;


BEGIN

if (nr_seq_nf_credito_p IS NOT NULL AND nr_seq_nf_credito_p::text <> '') then
	update	nf_credito_item
	set	pr_aplicacao = pr_valor_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_nf_credito = nr_seq_nf_credito_p;

	commit;

	open	c01;
	loop
	fetch	c01
	into	nr_seq_nf_credito_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL atualizar_valores_nf_cred_item(nr_seq_nf_credito_item_w, nm_usuario_p);
		end;
	end loop;
	close	c01;
end if;

end;
$body$
