SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE definir_senha_medico ( ie_senha_p bigint, nr_dias_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE


nm_usuario_w	varchar(15);


BEGIN

select	coalesce(max(nm_usuario),'0')
into STRICT	nm_usuario_w
from	usuario
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_situacao = 'A';

if (nm_usuario_w <> '0') then

	if (ie_senha_p = 0) then
		update  usuario
		set     ds_senha            = nm_usuario_w,
			dt_alteracao_senha   = NULL,
			dt_validade_usuario = CASE WHEN dt_validade_usuario = NULL THEN null  ELSE dt_validade_usuario + coalesce(to_char(somente_numero(nr_dias_p)),0) END
		where   cd_pessoa_fisica    = cd_pessoa_fisica_p;
	elsif (ie_senha_p = 1) then
		update  usuario
		set     ds_senha            = cd_pessoa_fisica_p,
			dt_alteracao_senha   = NULL,
			dt_validade_usuario = CASE WHEN dt_validade_usuario = NULL THEN null  ELSE dt_validade_usuario + coalesce(to_char(somente_numero(nr_dias_p)),0) END
		where   cd_pessoa_fisica    = cd_pessoa_fisica_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
