CREATE OR REPLACE PROCEDURE definir_setor_exclusivo_relac (ie_estrutura_p text, cd_estrutura_p bigint, cd_setor_exclusivo_p bigint, cd_estab_relac_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_setor_exclusivo_w		integer;
ds_setor_exclusivo_w		varchar(60);
ds_setor_exclusivo_destino_w	varchar(60);
ie_situacao_w			varchar(1);
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	estrutura_procedimento_v
	where	((ie_estrutura_p = 'A' AND cd_area_procedimento = cd_estrutura_p) or
		 (ie_estrutura_p = 'E' AND cd_especialidade = cd_estrutura_p) or
		 (ie_estrutura_p = 'G' AND cd_grupo_proc = cd_estrutura_p))
	order by cd_procedimento,
		 ie_origem_proced;


BEGIN

if (cd_setor_exclusivo_p IS NOT NULL AND cd_setor_exclusivo_p::text <> '' AND cd_estab_relac_p IS NOT NULL AND cd_estab_relac_p::text <> '') then
open C01;
loop
fetch C01 into
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	 select nextval('procedimento_setor_atend_seq')
	 into STRICT	nr_sequencia_w
	;

	insert into procedimento_setor_atend(	NR_SEQUENCIA,
						CD_PROCEDIMENTO,
						IE_ORIGEM_PROCED,
						DT_ATUALIZACAO,
						NM_USUARIO,
						CD_SETOR_ATENDIMENTO,
						IE_PRIORIDADE,
						DT_ATUALIZACAO_NREC,
						NM_USUARIO_NREC,
						CD_ESTABELECIMENTO,
						CD_SETOR_ORIGEM,
						IE_BANCO_SANGUE_REP)
	values (nr_sequencia_w,
			cd_procedimento_w,
			ie_origem_proced_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_setor_exclusivo_p,
			100,
			clock_timestamp(),
			nm_usuario_p,
			cd_estab_relac_p,
			null,
			'N');

	end;
end loop;
close C01;

end if;

commit;

end;
