CREATE OR REPLACE PROCEDURE definir_status_alteracao_nome ( nr_sequencia_p bigint, ie_status_p text, ie_cancelar_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nm_pessoa_fisica_w	varchar(60);
nm_pessoa_atual_w	varchar(60);


BEGIN

if (ie_status_p = 'A') then
	if (ie_cancelar_p = 'S') then
		update	pessoa_fisica_alteracao
		set	dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_aprovacao	 = NULL,
			nm_usuario_aprovacao  = NULL
		where	nr_sequencia	= nr_sequencia_p;
	else
		update	pessoa_fisica_alteracao
		set	dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_aprovacao	= clock_timestamp(),
			nm_usuario_aprovacao = nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;
	end if;
else
	select	max(cd_pessoa_fisica),
		max(nm_pessoa_fisica),
		max(nm_pessoa_atual)
	into STRICT	cd_pessoa_fisica_w,
		nm_pessoa_fisica_w,
		nm_pessoa_atual_w
	from	pessoa_fisica_alteracao
	where	nr_sequencia = nr_sequencia_p;

	insert into pessoa_fisica_historico(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			dt_liberacao,
			ds_historico)
		values (	nextval('pessoa_fisica_historico_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			clock_timestamp(),
			substr(wheb_mensagem_pck.get_texto(305441)|| nm_usuario_p,1,4000));

	if (ie_cancelar_p = 'S') then
		update	pessoa_fisica_alteracao
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_reprovacao  = NULL,
			nm_usuario_reprovacao  = NULL
		where	nr_sequencia = nr_sequencia_p;

		CALL exec_sql_dinamico('TASY','ALTER TRIGGER PESSOA_FISICA_BEFUPDATE_LOG DISABLE');

		update	pessoa_fisica
		set	nm_pessoa_fisica = nm_pessoa_atual_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;

		CALL exec_sql_dinamico('TASY','ALTER TRIGGER PESSOA_FISICA_BEFUPDATE_LOG ENABLE');
	else
		update	pessoa_fisica_alteracao
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_reprovacao = clock_timestamp(),
			nm_usuario_reprovacao = nm_usuario_p
		where	nr_sequencia = nr_sequencia_p;

		CALL exec_sql_dinamico('TASY','ALTER TRIGGER PESSOA_FISICA_BEFUPDATE_LOG DISABLE');

		update	pessoa_fisica
		set	nm_pessoa_fisica = nm_pessoa_fisica_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;

		CALL exec_sql_dinamico('TASY','ALTER TRIGGER PESSOA_FISICA_BEFUPDATE_LOG ENABLE');
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
