CREATE OR REPLACE PROCEDURE definir_suep_padrao ( nr_sequencia_suep_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


			/*
				D - Definir padrao
				L - Limpar padrao
			*/
ie_existe_regra_w 	varchar(1);


BEGIN

if (nr_sequencia_suep_p IS NOT NULL AND nr_sequencia_suep_p::text <> '') and (ie_acao_p = 'D') then

	select 	coalesce(max('S'),'N')
	into STRICT 	ie_existe_regra_w
	from 	suep_padrao_usuario
	where 	nr_seq_modelo_suep = nr_sequencia_suep_p
	and	nm_usuario_suep = nm_usuario_p;

	if (ie_existe_regra_w = 'S') then

		update	suep_padrao_usuario d
		set	nr_seq_modelo_suep = nr_sequencia_suep_p
		where	nm_usuario_suep = nm_usuario_p;

	else

		insert into suep_padrao_usuario(nr_sequencia ,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nm_usuario_suep,
						nr_seq_modelo_suep)
					values (nextval('suep_padrao_usuario_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nm_usuario_p,
						nr_sequencia_suep_p);

	end if;

	commit;

elsif (nr_sequencia_suep_p IS NOT NULL AND nr_sequencia_suep_p::text <> '') and (ie_acao_p = 'L') then

	delete 	from suep_padrao_usuario
	where	nm_usuario_suep = nm_usuario_p;


	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
