CREATE OR REPLACE PROCEDURE definir_tipo_ato_servico ( cd_estabelecimento_p bigint, cd_procedimento_p bigint, cd_convenio_p bigint, cd_medico_executor_p text, cd_cgc_prestador_p text, cd_tipo_ato_p INOUT bigint, cd_tipo_servico_p INOUT bigint) AS $body$
DECLARE


ie_tipo_ato_sus_w		smallint;
ie_tipo_servico_sus_w	smallint;
ie_conveniado_w		varchar(1)	:= 'N';
cd_grupo_proc_w		bigint;


c01 CURSOR FOR
SELECT	a.ie_tipo_ato_sus,
	a.ie_tipo_servico_sus
from	sus_proced_tipo_ato a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and (coalesce(a.cd_grupo_proc,cd_grupo_proc_w)	= cd_grupo_proc_w or coalesce(cd_grupo_proc_w::text, '') = '')
and (coalesce(a.cd_procedimento,cd_procedimento_p)	= cd_procedimento_p or coalesce(cd_procedimento_p::text, '') = '')
and (a.cd_cgc_prestador	= cd_cgc_prestador_p or coalesce(a.cd_cgc_prestador::text, '') = '')
and (a.cd_medico_executor	= cd_medico_executor_p or coalesce(a.cd_medico_executor::text, '') = '')
and (a.ie_conveniado	= ie_conveniado_w or a.ie_conveniado = 'T' or coalesce(a.ie_conveniado::text, '') = '')
order by
	coalesce(a.cd_procedimento,0),
	coalesce(a.cd_cgc_prestador,'0'),
	coalesce(a.cd_medico_executor,'0'),
	coalesce(a.ie_conveniado,'0');


BEGIN

ie_conveniado_w	:= obter_se_medico_credenciado(cd_estabelecimento_p, cd_medico_executor_p, cd_convenio_p, null, null, null,null, null,null, null, null, null);

begin
select	cd_grupo_proc
into STRICT	cd_grupo_proc_w
from 	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= 2;
exception
     when others then
        cd_grupo_proc_w	:= 0;
end;

open c01;
loop
fetch c01 into
		ie_tipo_ato_sus_w,
		ie_tipo_servico_sus_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ie_tipo_ato_sus_w	:= ie_tipo_ato_sus_w;
		ie_tipo_servico_sus_w	:= ie_tipo_servico_sus_w;
	end loop;
close c01;

if (cd_medico_executor_p IS NOT NULL AND cd_medico_executor_p::text <> '') 	and
	((coalesce(ie_tipo_servico_sus_w::text, '') = '') or (ie_tipo_servico_sus_w in (4,7,45)))	then
	begin
	select coalesce(max(ie_tipo_servico_sus),ie_tipo_servico_sus_w)
	into STRICT	 ie_tipo_servico_sus_w
	from	 medico_convenio
	where	 cd_convenio		= cd_convenio_p
	and	 cd_pessoa_fisica	= cd_medico_executor_p
	and	 ((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = cd_estabelecimento_p));
	end;
end if;

cd_tipo_ato_p			:= ie_tipo_ato_sus_w;
cd_tipo_servico_p		:= ie_tipo_servico_sus_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
