CREATE OR REPLACE PROCEDURE definir_tipo_procedimento ( ie_estrutura_p text, cd_estrutura_p bigint, cd_tipo_procedimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_tipo_procedimento_w		smallint;
ds_tipo_procedimento_w		varchar(255);
ds_tipo_procedimento_dest_w	varchar(255);
ie_situacao_w			varchar(1);
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	estrutura_procedimento_v
	where	((ie_estrutura_p = 'A' AND cd_area_procedimento = cd_estrutura_p) or
		 (ie_estrutura_p = 'E' AND cd_especialidade = cd_estrutura_p) or
		 (ie_estrutura_p = 'G' AND cd_grupo_proc = cd_estrutura_p))
	order by cd_procedimento,
		 ie_origem_proced;


BEGIN

open C01;
loop
fetch C01 into
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select 	max(cd_tipo_procedimento),
		max(substr(obter_valor_dominio(95, cd_tipo_procedimento),1,255)),
		max(substr(obter_valor_dominio(95, cd_tipo_procedimento_p),1,255)),
		coalesce(max(ie_situacao),'I')
	into STRICT	cd_tipo_procedimento_w,
		ds_tipo_procedimento_w,
		ds_tipo_procedimento_dest_w,
		ie_situacao_w
	from 	procedimento
	where 	cd_procedimento  = cd_procedimento_w
	and 	ie_origem_proced = ie_origem_proced_w;

	if (ie_situacao_w = 'A') then

		update	procedimento
		set 	cd_tipo_procedimento	= cd_tipo_procedimento_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where 	cd_procedimento		= cd_procedimento_w
		and 	ie_origem_proced	= ie_origem_proced_w;

		insert	into procedimento_hist(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_procedimento,
				ie_origem_proced,
				ds_titulo,
				ds_historico)
			values (nextval('procedimento_hist_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_procedimento_w,
				ie_origem_proced_w,
				upper(wheb_mensagem_pck.get_texto(799746)),
				wheb_mensagem_pck.get_texto(799747,'cd_tipo_procedimento_w=' || cd_tipo_procedimento_w || ';' ||
								   'ds_tipo_procedimento_w=' || ds_tipo_procedimento_w || ';' ||
								   'cd_tipo_procedimento_p=' || cd_tipo_procedimento_p || ';' ||
								   'ds_tipo_procedimento_dest_w=' || ds_tipo_procedimento_dest_w));

	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
