CREATE OR REPLACE PROCEDURE deletar_agenda_integrada_item (cd_pessoa_origem_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	nr_sequencia
from 	agenda_integrada_item
where	nr_seq_agenda_cons in (	select	nr_sequencia
				from 	agenda_consulta
				where	cd_pessoa_fisica = cd_pessoa_origem_p)
or	nr_seq_agenda_exame in (	select 	nr_sequencia
				from 	agenda_paciente
				where	cd_pessoa_fisica = cd_pessoa_origem_p);
c01_w	c01%rowtype;

c02 CURSOR FOR
SELECT	a.nm_tabela,
	c.nm_atributo
from	integridade_referencial a,
	tabela_sistema b,
	integridade_atributo c
where	a.nm_tabela = b.nm_tabela
and	c.nm_integridade_referencial = a.nm_integridade_referencial
and	a.nm_tabela_referencia = 'AGENDA_INTEGRADA_ITEM'
and	a.ie_regra_delecao = 'NO ACTION';
c02_w	c02%rowtype;


BEGIN

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

	open c02;
	loop
	fetch c02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		begin

		CALL exec_sql_dinamico('DELAGEINT','delete ' || c02_w.nm_tabela || ' where ' || c02_w.nm_atributo || ' = ' || c01_w.nr_sequencia);

		end;
	end loop;
	close c02;

	delete	from agenda_integrada_item
	where	nr_sequencia = c01_w.nr_sequencia;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
