CREATE OR REPLACE PROCEDURE deletar_anexo_aprovacoes ( nr_documento_p text, ie_opcao_p text, nr_seq_item_p bigint, ie_item_p bigint, ds_arquivo_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'S') then
	begin
	
	delete from solic_compra_item_anexo
	where	nr_solic_compra = nr_documento_p
	and		((ie_item_p = 0) or
		   (ie_item_p = 1 AND nr_item_solic_compra = nr_seq_item_p ))
	and		ds_arquivo	= ds_arquivo_p;
	
	end;
elsif (ie_opcao_p = 'O') then
	begin
	
	delete from ordem_compra_anexo
	where	nr_ordem_compra = nr_documento_p
	and		ds_arquivo		= ds_arquivo_p;
	
	end;
elsif (ie_opcao_p = 'L') then
	begin
	
	delete from reg_lic_anexo
	where	nr_seq_licitacao 	= nr_documento_p
	and		ds_arquivo			= ds_arquivo_p;
	
	end;
elsif (ie_opcao_p = 'C') then
	begin
	
	delete from cot_compra_anexo
	where	nr_cot_compra 	= nr_documento_p
	and		ds_arquivo			= ds_arquivo_p;
	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
